import functools

def cache_function(func):
    cache = {}

    @functools.wraps(func)
    def wrapper(*args, **kwargs):
        if args and kwargs:
            key = str(args) + ', ' + str(kwargs)
        elif args:
            key = str(args)
        elif kwargs:
            key = str(kwargs)
        else:
            key = None

        if key in cache:
            return cache[key]

        result = func(*args, **kwargs)
        cache[key] = result
        return result

    return wrapper

@cache_function
def fibonacci(n):
    if n < 2:
        return n
    else:
        return fibonacci(n-1) + fibonacci(n-2)

